import React, { useState, useEffect } from "react";

function App() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [allItems, setAllItems] = useState([]);
  const [sortKey, setSortKey] = useState(null);
  const [sortAsc, setSortAsc] = useState(true);

  // Fetch all foods once for suggestion filtering
  useEffect(() => {
    const fetchAll = async () => {
      try {
        const res = await fetch("http://127.0.0.1:8000/food/search?query=");
        const data = await res.json();
        setAllItems(data);
      } catch (err) {
        console.error("Failed to load suggestions", err);
      }
    };
    fetchAll();
  }, []);

  // Update suggestions as query changes
  useEffect(() => {
    if (!query) {
      setSuggestions([]);
      return;
    }

    const filtered = allItems
      .filter((item) =>
        item.name.toLowerCase().includes(query.toLowerCase())
      )
      .map((item) => item.name);

    const unique = [...new Set(filtered)].slice(0, 5);
    setSuggestions(unique);
  }, [query, allItems]);

  const handleSearch = async (q = query) => {
    try {
      const res = await fetch(`http://127.0.0.1:8000/food/search?query=${q}`);
      const data = await res.json();
      setResults(data);
      setSuggestions([]);
    } catch (err) {
      console.error("Error fetching data:", err);
    }
  };

  const handleSort = (key) => {
    if (sortKey === key) {
      setSortAsc(!sortAsc);
    } else {
      setSortKey(key);
      setSortAsc(true);
    }
  };

  const sortedResults = [...results].sort((a, b) => {
    if (!sortKey) return 0;
    if (typeof a[sortKey] === "string") {
      return sortAsc
        ? a[sortKey].localeCompare(b[sortKey])
        : b[sortKey].localeCompare(a[sortKey]);
    }
    return sortAsc ? a[sortKey] - b[sortKey] : b[sortKey] - a[sortKey];
  });

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial", textAlign: "center" }}>
      <h1>Smart Diet & Nutrition Planner</h1>

      <div style={{ position: "relative" }}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search food..."
          style={{ padding: "0.5rem", width: "250px" }}
        />
        <button onClick={() => handleSearch()} style={{ marginLeft: "1rem", padding: "0.5rem 1rem" }}>
          Search
        </button>
        {suggestions.length > 0 && (
          <ul style={{
            listStyle: "none",
            padding: "0",
            margin: "0",
            border: "1px solid #ccc",
            width: "250px",
            backgroundColor: "#fff",
            position: "absolute",
            zIndex: 10,
            left: "0"
          }}>
            {suggestions.map((sug, idx) => (
              <li
                key={idx}
                onClick={() => {
                  setQuery(sug);
                  handleSearch(sug);
                }}
                style={{
                  padding: "0.5rem",
                  borderBottom: "1px solid #eee",
                  cursor: "pointer",
                  textAlign: "left"
                }}
              >
                {sug}
              </li>
            ))}
          </ul>
        )}
      </div>

      {sortedResults.length > 0 ? (
        <table style={{ marginTop: "2rem", borderCollapse: "collapse", width: "90%", margin: "2rem auto" }}>
          <thead>
            <tr>
              {["name", "calories", "protein", "carbs", "fat"].map((key) => (
                <th
                  key={key}
                  onClick={() => handleSort(key)}
                  style={{ ...thStyle, cursor: "pointer" }}
                >
                  {key.charAt(0).toUpperCase() + key.slice(1)}
                  {sortKey === key ? (sortAsc ? " ▲" : " ▼") : ""}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {sortedResults.map((item) => (
              <tr key={item.id}>
                <td style={tdStyle}>{item.name}</td>
                <td style={tdStyle}>{item.calories}</td>
                <td style={tdStyle}>{item.protein}</td>
                <td style={tdStyle}>{item.carbs}</td>
                <td style={tdStyle}>{item.fat}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ marginTop: "2rem" }}>No results found.</p>
      )}
    </div>
  );
}

const thStyle = {
  border: "1px solid #ccc",
  padding: "0.75rem",
  backgroundColor: "#f9f9f9",
  textTransform: "capitalize",
};

const tdStyle = {
  border: "1px solid #ccc",
  padding: "0.75rem",
};

export default App;
