import React, { useState } from "react";

function App() {
  const [query, setQuery] = useState("Apple");
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    try {
      const res = await fetch(`http://127.0.0.1:8000/food/search?query=${query}`);
      const data = await res.json();
      setResults(data);
    } catch (error) {
      console.error("Error fetching data:", error);
      setResults([]);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial", textAlign: "center" }}>
      <h1>Smart Diet & Nutrition Planner</h1>
      <input
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search food..."
        style={{ padding: "0.5rem", width: "200px", marginRight: "0.5rem" }}
      />
      <button onClick={handleSearch} style={{ padding: "0.5rem 1rem" }}>
        Search
      </button>

      <div style={{ marginTop: "2rem" }}>
        {results.length > 0 ? (
          <table style={{ margin: "0 auto", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th style={thStyle}>Name</th>
                <th style={thStyle}>Calories</th>
                <th style={thStyle}>Protein</th>
                <th style={thStyle}>Carbs</th>
                <th style={thStyle}>Fat</th>
              </tr>
            </thead>
            <tbody>
              {results.map((item) => (
                <tr key={item.id}>
                  <td style={tdStyle}>{item.name}</td>
                  <td style={tdStyle}>{item.calories}</td>
                  <td style={tdStyle}>{item.protein}</td>
                  <td style={tdStyle}>{item.carbs}</td>
                  <td style={tdStyle}>{item.fat}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p style={{ marginTop: "1rem" }}>No results found.</p>
        )}
      </div>
    </div>
  );
}

const thStyle = {
  border: "1px solid black",
  padding: "0.5rem",
  backgroundColor: "#f2f2f2",
};

const tdStyle = {
  border: "1px solid black",
  padding: "0.5rem",
};

export default App;
